mapper.select:
select * from (select row_.*, rownum rownum_ from (select p.id, p.plan_id planId, p.plan_title planTitle, p.plan_address planAddress, p.plan_start_date planStartDate, p.plan_end_date planEndDate, t.open_id openId, t.nick_name nickName, t.avatar_url avatarUrl, t.sign_address signAddress, t.sign_date signDate from market.market_wx_admin_info t left join market.market_tb_plan_activity p on t.activity_id = to_char(p.id)
and p.plan_id = #{planId} and p.id = #{id} and p.plan_title LIKE '%${planTitle}%' and p.plan_address LIKE '%${planAddress}%' and p.plan_start_date = #{planStartDate} and p.plan_end_date = #{planEndDate} and t.status in ('N','Y')
order by ${sort} ${order}
order by t.id desc
) row_ where rownum <= ${limit}) where rownum_ > ${offset}

select COUNT (*)
from market.market_wx_admin_info t
left join market.market_tb_plan_activity p on t.activity_id = to_char(p.id)

    and p.plan_id = #{planId}
    and p.id = #{id}
    and p.plan_title LIKE '%${planTitle}%'
    and p.plan_address LIKE '%${planAddress}%'
    and p.plan_start_date = #{planStartDate}
    and p.plan_end_date = #{planEndDate}

select * from (select row_.*, rownum rownum_ from (select p.id, p.plan_id planId, p.plan_title planTitle, p.plan_address planAddress, p.plan_start_date planStartDate, p.plan_end_date planEndDate, t.open_id openId, t.nick_name nickName, t.avatar_url avatarUrl, case (t.status) when 'N' then '未参加' when 'Y' then '已参加' end status, case (t.is_transmit) when 'N' then '未转发' when 'Y' then '已转发' end isTransmit from market.market_wx_user_info t left join market.market_tb_plan_activity p on t.activity_id = p.id
and p.plan_id = #{planId} and p.id = #{id} and p.plan_title LIKE '%${planTitle}%' and p.plan_address LIKE '%${planAddress}%' and p.plan_start_date = #{planStartDate} and p.plan_end_date = #{planEndDate} and t.status in ('N','Y') and t.status = 'Y' and t.status = 'N'
order by ${sort} ${order}
order by t.id desc
) row_ where rownum <= ${limit}) where rownum_ > ${offset}

select COUNT (*)
from market.market_wx_user_info t
left join market.market_tb_plan_activity p on t.activity_id = p.id

    and p.plan_id = #{planId}
    and p.id = #{id}
    and p.plan_title LIKE '%${planTitle}%'
    and p.plan_address LIKE '%${planAddress}%'
    and p.plan_start_date = #{planStartDate}
    and p.plan_end_date = #{planEndDate}
    and t.status in ('N','Y')
    and t.status = 'Y'
    and t.status = 'N'

select * from (select row_.*, rownum rownum_ from (select p.id, p.plan_id planId, p.plan_title planTitle, p.plan_address planAddress, p.plan_start_date planStartDate, p.plan_end_date planEndDate, tt.open_id openId, tt.nick_name nickName, tt.avatar_url avatarUrl, pp.photo_url photoUrl, pp.creart_date creartDate from market.markrt_wx_plan_photo pp left join (select distinct (t.activity_id) activity_id, t.open_id, t.nick_name, t.avatar_url from market.market_wx_admin_info t) tt on tt.open_id = pp.openid and tt.activity_id = pp.activity_id left join market.market_tb_plan_activity p on tt.activity_id = p.id
and p.plan_id = #{planId} and p.id = #{id} and p.plan_title LIKE '%${planTitle}%' and p.plan_address LIKE '%${planAddress}%' and p.plan_start_date = #{planStartDate} and p.plan_end_date = #{planEndDate} and t.status in ('N','Y')
order by ${sort} ${order}
order by pp.creart_date desc
) row_ where rownum <= ${limit}) where rownum_ > ${offset}
